import React, { useState, useEffect } from "react";
import Card from "./Card";
import Pokeinfo from "./Pokeinfo";
import axios from "axios";

const Main = () => {
  const [pokeData, setPokeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [nextUrl, setNextUrl] = useState(null);
  const [prevUrl, setPrevUrl] = useState(null);

  const fetchPokemonData = async (url) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(url);
      setPokeData(response.data);
      setNextUrl(response.data.next);
      setPrevUrl(response.data.previous);
    } catch (error) {
      console.error(error);
      setError("Error fetching pokemon data");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPokemonData("http://localhost:3001/pokemon");
  }, []);

  const handlePrevClick = () => {
    fetchPokemonData(prevUrl);
  };

  const handleNextClick = () => {
    fetchPokemonData(nextUrl);
  };

  return (
    <>
      <div className="container">
        <div className="left-content">
          {loading ? (
            <p>Loading...</p>
          ) : (
            <Card pokemon={pokeData} loading={loading} infoPokemon={(poke) => setPokeDex(poke)} />
          )}

          <div className="btn-group">
            {prevUrl && <button onClick={handlePrevClick}>Previous</button>}
            {nextUrl && <button onClick={handleNextClick}>Next</button>}
          </div>
        </div>
        <div className="right-content">
          <Pokeinfo data={pokeDex} />
        </div>
      </div>
    </>
  );
};

export default Main;
