// Main.js
import React, { useState, useEffect } from "react";
import Card from "./Card";
import Pokeinfo from "./Pokeinfo";
import axios from "axios";

const Main = () => {
  const [pokeData, setPokeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [nextUrl, setNextUrl] = useState();
  const [prevUrl, setPrevUrl] = useState();
  const [pokeDex, setPokeDex] = useState();

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const response = await axios.get("http://localhost:5000/pokemon");
        setNextUrl(response.data.next);
        setPrevUrl(response.data.previous);
        setPokeData(response.data.results);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleSelectPokemon = async (url) => {
    try {
      const response = await axios.get(url);
      setPokeDex(response.data);
    } catch (error) {
      console.error("Error fetching Pokemon details:", error);
    }
  };

  return (
    <>
      <div className="container">
        <div className="left-content">
          <Card pokemon={pokeData} loading={loading} infoPokemon={handleSelectPokemon} />

          <div className="btn-group">
            {prevUrl && (
              <button
                onClick={async () => {
                  setLoading(true);
                  try {
                    const response = await axios.get(prevUrl);
                    setPokeData(response.data.results);
                    setNextUrl(response.data.next);
                    setPrevUrl(response.data.previous);
                    setLoading(false);
                  } catch (error) {
                    console.error("Error fetching previous page:", error);
                    setLoading(false);
                  }
                }}
              >
                Previous
              </button>
            )}
            {nextUrl && (
              <button
                onClick={async () => {
                  setLoading(true);
                  try {
                    const response = await axios.get(nextUrl);
                    setPokeData(response.data.results);
                    setNextUrl(response.data.next);
                    setPrevUrl(response.data.previous);
                    setLoading(false);
                  } catch (error) {
                    console.error("Error fetching next page:", error);
                    setLoading(false);
                  }
                }}
              >
                Next
              </button>
            )}
          </div>
        </div>
        <div className="right-content">
          <Pokeinfo data={pokeDex} />
        </div>
      </div>
    </>
  );
};

export default Main;
